function onbefore(component){
    console.log("This is a " + component.getId());
}

function onafter(widget){
    console.log("This is a widget" + widget.getId());
    for(p in widget){
        console.log(p);
    }
    widget.setBackgroundColor(-500136);
    widget.getInputView().setTextColor(-500136);
    widget.getInputView().setText("Hello world from rhino JS!");
}
